import React, { lazy, Suspense, useEffect, useState } from "react";
import Header        from "../../components/header/Header";
import WorkExperience from "../workExperience/WorkExperience";
import Education      from "../education/Education";
import Achievement    from "../achievement/Achievement";
import Blogs          from "../blogs/Blogs";
import Talks          from "../talks/Talks";
import Podcast        from "../podcast/Podcast";
import Profile        from "../profile/Profile";
import Footer         from "../../components/footer/Footer";
import Loading        from "../loading/Loading";
import "./About.scss";

const Greeting = lazy(() => import("../greeting/Greeting"));

/* ▼ 1‑liner placeholder image
   OPTION A — local asset.
   Put an SVG or PNG at  src/assets/placeholder-avatar.svg  (create folder if needed)
*/
import defaultAvatar from "..";

/* OPTION B — comment out the line above and uncomment the one below
   to use a remote placeholder instead of adding a local file.

const defaultAvatar = "https://via.placeholder.com/160x160.svg?text=…";
*/

const emptyProfile = {
  id:        0,
  name:      "Loading…",
  avatarUrl: defaultAvatar,
};

export default function About() {
  const [profile, setProfile] = useState(emptyProfile);

  useEffect(() => {
    const abort = new AbortController();
    const githubUser = "octocat";              // ← put your handle here

    (async () => {
      try {
        const r = await fetch(
          `https://api.github.com/users/${githubUser}`,
          { signal: abort.signal }
        );
        if (r.ok) {
          const j = await r.json();
          setProfile({
            id:        j.id,
            name:      j.name ?? j.login,
            avatarUrl: j.avatar_url,
          });
        }
      } catch (e) {
        if (e.name !== "AbortError") console.error(e);
      }
    })();

    return () => abort.abort();
  }, []);

  return (
    <>
      <Header />
      <main className="about">
        <Suspense fallback={<Loading />}>
          <Greeting prof={profile} />
        </Suspense>

        <WorkExperience />
        <Education />
        <Achievement />
        <Blogs />
        <Talks />
        <Podcast />
        <Profile />
      </main>
      <Footer />
    </>
  );
}
