import React, { lazy, Suspense, useEffect, useMemo, useState } from "react";
import Header from "../../components/header/Header";
import "./About.scss";
import Education from "../education/Education";
import WorkExperience from "../workExperience/WorkExperience";
import Achievement from "../achievement/Achievement";
import Blogs from "../blogs/Blogs";
import Talks from "../talks/Talks";
import Podcast from "../podcast/Podcast";
import Profile from "../profile/Profile";
import Footer from "../../components/footer/Footer";
import Loading from "../loading/Loading";

function About() {
  const Greeting = useMemo(
    () => lazy(() => import("../greeting/Greeting")),
    []
  );

  const [prof, setProf] = useState(null);

  useEffect(() => {
    // sample async load — replace <username> with real value or your own loader
    const controller = new AbortController();

    (async () => {
      try {
        const res = await fetch(
          "https://api.github.com/users/<username>",
          { signal: controller.signal }
        );
        if (!res.ok) return;          // early return, no `else`
        const json = await res.json();
        setProf({
          id: json.id,
          name: json.name ?? json.login,
          avatarUrl: json.avatar_url,
        });
      } catch (err) {
        if (err.name !== "AbortError") console.error(err);
      }
    })();

    return () => controller.abort();   // clean‑up
  }, []);

  return (
    <>
      <Header />

      <main className="about">
        <Suspense fallback={<Loading />}>
          {prof && <Greeting prof={prof} />}   {/* render only when ready */}
        </Suspense>

        <WorkExperience />
        <Education />
        <Achievement />
        <Blogs />
        <Talks />
        <Podcast />
        <Profile />
      </main>

      <Footer />
    </>
  );
}

export default About;
