import React, { lazy, Suspense, useEffect, useState } from "react";
import Header        from "../../components/header/Header";
import WorkExperience from "../workExperience/WorkExperience";
import Education      from "../education/Education";
import Achievement    from "../achievement/Achievement";
import Blogs          from "../blogs/Blogs";
import Talks          from "../talks/Talks";
import Podcast        from "../podcast/Podcast";
import Profile        from "../profile/Profile";
import Footer         from "../../components/footer/Footer";
import Loading        from "../loading/Loading";
import "./About.scss";

// ─── Lazy‑loaded greeting ────────────────────────────────────────────────────
const Greeting = lazy(() => import("../greeting/Greeting"));

// ─── Immediate placeholder so Greeting never sees `null` ─────────────────────
const emptyProfile = {
  id:        0,
  name:      "Loading…",
  avatarUrl: require("../../assets/placeholder-avatar.svg").default,
};

function About() {
  const [profile, setProfile] = useState(emptyProfile);

  useEffect(() => {
    const controller = new AbortController();
    const githubUser = "octocat";            // ← replace with your handle

    (async () => {
      try {
        const res = await fetch(
          `https://api.github.com/users/${githubUser}`,
          { signal: controller.signal }
        );
        if (res.ok) {
          const j = await res.json();
          setProfile({
            id:        j.id,
            name:      j.name ?? j.login,
            avatarUrl: j.avatar_url,
          });
        }
      } catch (err) {
        if (err.name !== "AbortError") console.error(err);
      }
    })();

    return () => controller.abort();
  }, []);

  console.log("profile state →", profile);

  return (
    <>
      <Header />

      <main className="about">
        <Suspense fallback={<Loading />}>
          <Greeting prof={profile} />
        </Suspense>

        <WorkExperience />
        <Education />
        <Achievement />
        <Blogs />
        <Talks />
        <Podcast />
        <Profile />
      </main>

      <Footer />
    </>
  );
}

export default About;
